import { createAnatomy } from "@zag-js/anatomy"

export const anatomy = createAnatomy("numberInput").parts(
  "root",
  "label",
  "input",
  "control",
  "valueText",
  "incrementTrigger",
  "decrementTrigger",
  "scrubber",
)

export const parts = anatomy.build()
import { getEventPoint, getEventStep, isLeftClick, type EventKeyMap } from "@zag-js/dom-event"
import { ariaAttr, dataAttr, isComposingEvent } from "@zag-js/dom-query"
import { roundToDevicePixel } from "@zag-js/number-utils"
import type { NormalizeProps, PropTypes } from "@zag-js/types"
import { parts } from "./number-input.anatomy"
import { dom } from "./number-input.dom"
import type { MachineApi, Send, State } from "./number-input.types"

export function connect<T extends PropTypes>(state: State, send: Send, normalize: NormalizeProps<T>): MachineApi<T> {
  const focused = state.hasTag("focus")
  const disabled = state.context.isDisabled
  const readOnly = state.context.readOnly

  const empty = state.context.isValueEmpty
  const invalid = state.context.isOutOfRange || !!state.context.invalid

  const isIncrementDisabled = disabled || !state.context.canIncrement || readOnly
  const isDecrementDisabled = disabled || !state.context.canDecrement || readOnly

  const translations = state.context.translations

  return {
    focused: focused,
    invalid: invalid,
    empty: empty,
    value: state.context.formattedValue,
    valueAsNumber: state.context.valueAsNumber,
    setValue(value) {
      send({ type: "VALUE.SET", value })
    },
    clearValue() {
      send("VALUE.CLEAR")
    },
    increment() {
      send("VALUE.INCREMENT")
    },
    decrement() {
      send("VALUE.DECREMENT")
    },
    setToMax() {
      send({ type: "VALUE.SET", value: state.context.max })
    },
    setToMin() {
      send({ type: "VALUE.SET", value: state.context.min })
    },
    focus() {
      dom.getInputEl(state.context)?.focus()
    },

    getRootProps() {
      return normalize.element({
        id: dom.getRootId(state.context),
        ...parts.root.attrs,
        dir: state.context.dir,
        "data-disabled": dataAttr(disabled),
        "data-focus": dataAttr(focused),
        "data-invalid": dataAttr(invalid),
      })
    },

    getLabelProps() {
      return normalize.label({
        ...parts.label.attrs,
        dir: state.context.dir,
        "data-disabled": dataAttr(disabled),
        "data-focus": dataAttr(focused),
        "data-invalid": dataAttr(invalid),
        id: dom.getLabelId(state.context),
        htmlFor: dom.getInputId(state.context),
      })
    },

    getControlProps() {
      return normalize.element({
        ...parts.control.attrs,
        dir: state.context.dir,
        role: "group",
        "aria-disabled": disabled,
        "data-focus": dataAttr(focused),
        "data-disabled": dataAttr(disabled),
        "data-invalid": dataAttr(invalid),
        "aria-invalid": ariaAttr(state.context.invalid),
      })
    },

    getValueTextProps() {
      return normalize.element({
        ...parts.valueText.attrs,
        dir: state.context.dir,
        "data-disabled": dataAttr(disabled),
        "data-invalid": dataAttr(invalid),
        "data-focus": dataAttr(focused),
      })
    },

    getInputProps() {
      return normalize.input({
        ...parts.input.attrs,
        dir: state.context.dir,
        name: state.context.name,
        form: state.context.form,
        id: dom.getInputId(state.context),
        role: "spinbutton",
        defaultValue: state.context.formattedValue,
        pattern: state.context.pattern,
        inputMode: state.context.inputMode,
        "aria-invalid": ariaAttr(invalid),
        "data-invalid": dataAttr(invalid),
        disabled,
        "data-disabled": dataAttr(disabled),
        readOnly: state.context.readOnly,
        required: state.context.required,
        autoComplete: "off",
        autoCorrect: "off",
        spellCheck: "false",
        type: "text",
        "aria-roledescription": "numberfield",
        "aria-valuemin": state.context.min,
        "aria-valuemax": state.context.max,
        "aria-valuenow": Number.isNaN(state.context.valueAsNumber) ? undefined : state.context.valueAsNumber,
        "aria-valuetext": state.context.valueText,
        onFocus() {
          send("INPUT.FOCUS")
        },
        onBlur() {
          send({ type: "INPUT.COMMIT", src: "blur" })
        },
        onChange(event) {
          send({ type: "INPUT.CHANGE", target: event.currentTarget, hint: "set" })
        },
        onBeforeInput(event) {
          try {
            const { selectionStart, selectionEnd, value } = event.currentTarget

            const nextValue = value.slice(0, selectionStart!) + ((event as any).data ?? "") + value.slice(selectionEnd!)
            const isValid = state.context.parser.isValidPartialNumber(nextValue)

            if (!isValid) {
              event.preventDefault()
            }
          } catch {
            // noop
          }
        },
        onKeyDown(event) {
          if (event.defaultPrevented) return
          if (readOnly) return
          if (isComposingEvent(event)) return

          const step = getEventStep(event) * state.context.step

          const keyMap: EventKeyMap = {
            ArrowUp() {
              send({ type: "INPUT.ARROW_UP", step })
              event.preventDefault()
            },
            ArrowDown() {
              send({ type: "INPUT.ARROW_DOWN", step })
              event.preventDefault()
            },
            Home() {
              send("INPUT.HOME")
              event.preventDefault()
            },
            End() {
              send("INPUT.END")
              event.preventDefault()
            },
            Enter() {
              send({ type: "INPUT.COMMIT", src: "enter" })
            },
          }

          const exec = keyMap[event.key]
          exec?.(event)
        },
      })
    },

    getDecrementTriggerProps() {
      return normalize.button({
        ...parts.decrementTrigger.attrs,
        dir: state.context.dir,
        id: dom.getDecrementTriggerId(state.context),
        disabled: isDecrementDisabled,
        "data-disabled": dataAttr(isDecrementDisabled),
        "aria-label": translations.decrementLabel,
        type: "button",
        tabIndex: -1,
        "aria-controls": dom.getInputId(state.context),
        onPointerDown(event) {
          if (isDecrementDisabled || !isLeftClick(event)) return
          send({ type: "TRIGGER.PRESS_DOWN", hint: "decrement", pointerType: event.pointerType })
          if (event.pointerType === "mouse") {
            event.preventDefault()
          }
          if (event.pointerType === "touch") {
            event.currentTarget?.focus({ preventScroll: true })
          }
        },
        onPointerUp(event) {
          send({ type: "TRIGGER.PRESS_UP", hint: "decrement", pointerType: event.pointerType })
        },
        onPointerLeave() {
          if (isDecrementDisabled) return
          send({ type: "TRIGGER.PRESS_UP", hint: "decrement" })
        },
      })
    },

    getIncrementTriggerProps() {
      return normalize.button({
        ...parts.incrementTrigger.attrs,
        dir: state.context.dir,
        id: dom.getIncrementTriggerId(state.context),
        disabled: isIncrementDisabled,
        "data-disabled": dataAttr(isIncrementDisabled),
        "aria-label": translations.incrementLabel,
        type: "button",
        tabIndex: -1,
        "aria-controls": dom.getInputId(state.context),
        onPointerDown(event) {
          if (isIncrementDisabled || !isLeftClick(event)) return
          send({ type: "TRIGGER.PRESS_DOWN", hint: "increment", pointerType: event.pointerType })
          if (event.pointerType === "mouse") {
            event.preventDefault()
          }
          if (event.pointerType === "touch") {
            event.currentTarget?.focus({ preventScroll: true })
          }
        },
        onPointerUp(event) {
          send({ type: "TRIGGER.PRESS_UP", hint: "increment", pointerType: event.pointerType })
        },
        onPointerLeave(event) {
          send({ type: "TRIGGER.PRESS_UP", hint: "increment", pointerType: event.pointerType })
        },
      })
    },

    getScrubberProps() {
      return normalize.element({
        ...parts.scrubber.attrs,
        dir: state.context.dir,
        "data-disabled": dataAttr(disabled),
        id: dom.getScrubberId(state.context),
        role: "presentation",
        onMouseDown(event) {
          if (disabled) return

          const point = getEventPoint(event)

          point.x = point.x - roundToDevicePixel(7.5)
          point.y = point.y - roundToDevicePixel(7.5)

          send({ type: "SCRUBBER.PRESS_DOWN", point })
          event.preventDefault()
        },
        style: {
          cursor: disabled ? undefined : "ew-resize",
        },
      })
    },
  }
}
import { createScope, isSafari, MAX_Z_INDEX } from "@zag-js/dom-query"
import { roundToDevicePixel, wrap } from "@zag-js/number-utils"
import type { MachineContext as Ctx } from "./number-input.types"

export const dom = createScope({
  getRootId: (ctx: Ctx) => ctx.ids?.root ?? `number-input:${ctx.id}`,
  getInputId: (ctx: Ctx) => ctx.ids?.input ?? `number-input:${ctx.id}:input`,
  getIncrementTriggerId: (ctx: Ctx) => ctx.ids?.incrementTrigger ?? `number-input:${ctx.id}:inc`,
  getDecrementTriggerId: (ctx: Ctx) => ctx.ids?.decrementTrigger ?? `number-input:${ctx.id}:dec`,
  getScrubberId: (ctx: Ctx) => ctx.ids?.scrubber ?? `number-input:${ctx.id}:scrubber`,
  getCursorId: (ctx: Ctx) => `number-input:${ctx.id}:cursor`,
  getLabelId: (ctx: Ctx) => ctx.ids?.label ?? `number-input:${ctx.id}:label`,

  getInputEl: (ctx: Ctx) => dom.getById<HTMLInputElement>(ctx, dom.getInputId(ctx)),
  getIncrementTriggerEl: (ctx: Ctx) => dom.getById<HTMLButtonElement>(ctx, dom.getIncrementTriggerId(ctx)),
  getDecrementTriggerEl: (ctx: Ctx) => dom.getById<HTMLButtonElement>(ctx, dom.getDecrementTriggerId(ctx)),
  getScrubberEl: (ctx: Ctx) => dom.getById(ctx, dom.getScrubberId(ctx)),
  getCursorEl: (ctx: Ctx) => dom.getDoc(ctx).getElementById(dom.getCursorId(ctx)),

  getPressedTriggerEl: (ctx: Ctx, hint = ctx.hint) => {
    let btnEl: HTMLButtonElement | null = null
    if (hint === "increment") {
      btnEl = dom.getIncrementTriggerEl(ctx)
    }
    if (hint === "decrement") {
      btnEl = dom.getDecrementTriggerEl(ctx)
    }
    return btnEl
  },

  setupVirtualCursor(ctx: Ctx) {
    if (isSafari()) return
    dom.createVirtualCursor(ctx)
    return () => {
      dom.getCursorEl(ctx)?.remove()
    }
  },

  preventTextSelection(ctx: Ctx) {
    const doc = dom.getDoc(ctx)
    const html = doc.documentElement
    const body = doc.body

    body.style.pointerEvents = "none"
    html.style.userSelect = "none"
    html.style.cursor = "ew-resize"

    return () => {
      body.style.pointerEvents = ""
      html.style.userSelect = ""
      html.style.cursor = ""
      if (!html.style.length) {
        html.removeAttribute("style")
      }
      if (!body.style.length) {
        body.removeAttribute("style")
      }
    }
  },

  getMousementValue(ctx: Ctx, event: MouseEvent) {
    const x = roundToDevicePixel(event.movementX)
    const y = roundToDevicePixel(event.movementY)

    let hint = x > 0 ? "increment" : x < 0 ? "decrement" : null

    if (ctx.isRtl && hint === "increment") hint = "decrement"
    if (ctx.isRtl && hint === "decrement") hint = "increment"

    const point = {
      x: ctx.scrubberCursorPoint!.x + x,
      y: ctx.scrubberCursorPoint!.y + y,
    }

    const win = dom.getWin(ctx)
    const width = win.innerWidth
    const half = roundToDevicePixel(7.5)
    point.x = wrap(point.x + half, width) - half

    return { hint, point }
  },

  createVirtualCursor(ctx: Ctx) {
    const doc = dom.getDoc(ctx)
    const el = doc.createElement("div")
    el.className = "scrubber--cursor"
    el.id = dom.getCursorId(ctx)

    Object.assign(el.style, {
      width: "15px",
      height: "15px",
      position: "fixed",
      pointerEvents: "none",
      left: "0px",
      top: "0px",
      zIndex: MAX_Z_INDEX,
      transform: ctx.scrubberCursorPoint
        ? `translate3d(${ctx.scrubberCursorPoint.x}px, ${ctx.scrubberCursorPoint.y}px, 0px)`
        : undefined,
      willChange: "transform",
    })

    el.innerHTML = `
        <svg width="46" height="15" style="left: -15.5px; position: absolute; top: 0; filter: drop-shadow(rgba(0, 0, 0, 0.4) 0px 1px 1.1px);">
          <g transform="translate(2 3)">
            <path fill-rule="evenodd" d="M 15 4.5L 15 2L 11.5 5.5L 15 9L 15 6.5L 31 6.5L 31 9L 34.5 5.5L 31 2L 31 4.5Z" style="stroke-width: 2px; stroke: white;"></path>
            <path fill-rule="evenodd" d="M 15 4.5L 15 2L 11.5 5.5L 15 9L 15 6.5L 31 6.5L 31 9L 34.5 5.5L 31 2L 31 4.5Z"></path>
          </g>
        </svg>`

    doc.body.appendChild(el)
  },
})
import { choose, createMachine, guards } from "@zag-js/core"
import { addDomEvent, requestPointerLock } from "@zag-js/dom-event"
import { isSafari, observeAttributes, raf } from "@zag-js/dom-query"
import { trackFormControl } from "@zag-js/form-utils"
