import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/authcontext.mjs';
import 'bootstrap/dist/css/bootstrap.min.css';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    identity: '',
    accountID: '',
    secret: '',
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const { identity, accountID, secret } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('https://localhost:5000/api/users/login', formData);
      login(res.data.token); // Update the AuthContext

      const payload = JSON.parse(atob(res.data.token.split('.')[1]));
      alert('Login successful. Redirecting...');

      // Redirect based on user type
      setTimeout(() => {
        if (payload.role === 'staff') {
          navigate('/payments');
        } else {
          navigate('/dashboard');
        }
      }, 2000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Login failed.');
      alert(error.response?.data?.message || 'Invalid credentials. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="text-center vh-100 gradient-custom">
      <div
        className="p-5 bg-image"
        style={{
          backgroundImage: "url('https://mdbootstrap.com/img/new/textures/full/171.jpg')",
          height: '300px',
        }}
      ></div>

      <div
        className="card mx-4 mx-md-5 shadow-5-strong bg-light"
        style={{
          marginTop: '-100px',
          backdropFilter: 'blur(30px)',
        }}
      >
        <div className="card-body py-5 px-md-5">
          <div className="row d-flex justify-content-center">
            <div className="col-lg-8">
              <h2 className="fw-bold mb-5 text-primary">Login to Your Account</h2>
              {message && (
                <div className="alert alert-warning" role="alert">
                  <strong>Error:</strong> {message}
                </div>
              )}
              <form onSubmit={onSubmit}>
                <div className="form-outline mb-4">
                  <input
                    type="text"
                    name="identity"
                    value={identity}
                    onChange={onChange}
                    placeholder="Enter your ID Number"
                    className="form-control"
                  />
                  <label className="form-label" htmlFor="identity">ID Number</label>
                </div>

                <div className="form-outline mb-4">
                  <input
                    type="text"
                    name="accountID"
                    value={accountID}
                    onChange={onChange}
                    placeholder="Enter your Account Number"
                    className="form-control"
                  />
                  <label className="form-label" htmlFor="accountID">Account Number</label>
                </div>

                <div className="form-outline mb-4">
                  <input
                    type="password"
                    name="secret"
                    value={secret}
                    onChange={onChange}
                    placeholder="Enter your secret"
                    className="form-control"
                  />
                  <label className="form-label" htmlFor="secret">Password</label>
                </div>

                <button
                  type="submit"
                  className="btn btn-primary btn-block mb-4"
                  disabled={loading}
                >
                  {loading ? 'Logging in...' : 'Login'}
                </button>
              </form>

              <p className="mb-0 mt-4">
                Don't have an account?{' '}
                <Link to="/register" className="fw-bold">
                  Register here
                </Link>
              </p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
