import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Payments = () => {
    const [payments, setPayments] = useState([]);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');
    const [userRole, setUserRole] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        // Decode role information here if available in token payload, otherwise set directly
        setUserRole(localStorage.getItem('role')); // Example role retrieval
        fetchPayments();
    }, []);

    const fetchPayments = async () => {
        setLoading(true);
        const token = localStorage.getItem('token');
        try {
            const res = await axios.get('https://localhost:5000/api/payments', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            setPayments(res.data);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Failed to fetch payments.');
        } finally {
            setLoading(false);
        }
    };

    const verifyPayment = async (id) => {
        const token = localStorage.getItem('token');
        try {
            const res = await axios.put(`https://localhost:5000/api/payments/${id}/approve`, {}, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            alert('Payment Verified: ' + res.data.message);
            fetchPayments();
        } catch (error) {
            alert('Verification Failed: ' + (error.response?.data?.message || 'Verification failed.'));
        }
    };

    return (
        <div>
            <h1>Payments Portal</h1>
            {message && <p>{message}</p>}
            {loading ? (
                <div>Loading...</div>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>Customer</th>
                            <th>Amount</th>
                            <th>Currency</th>
                            <th>Provider</th>
                            <th>Payee Account</th>
                            <th>SWIFT Code</th>
                            <th>Status</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {payments.map((payment) => (
                            <tr key={payment._id}>
                                <td>{payment.client?.name || 'N/A'}</td>
                                <td>{payment.amount}</td>
                                <td>{payment.currency}</td>
                                <td>{payment.provider}</td>
                                <td>{payment.payeeAccount}</td>
                                <td>{payment.swiftCode}</td>
                                <td>{payment.status === 'pending' ? 'Pending' : 'Verified'}</td>
                                <td>
                                    {userRole === 'staff' && payment.status === 'pending' && (
                                        <button onClick={() => verifyPayment(payment._id)}>Verify</button>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default Payments;
