import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

const Register = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    fullName: '',
    idNumber: '',
    accountNumber: '',
    password: '',
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const { fullName, idNumber, accountNumber, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('https://localhost:5000/api/users/register', formData);
      setMessage(res.data.message);
      alert('Account created. Your account has been successfully created!');
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Registration failed.');
      alert('Error: ' + (error.response?.data?.message || 'An error occurred during registration.'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="vh-100 gradient-custom">
      <div className="container py-5 h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-12 col-md-8 col-lg-6 col-xl-5">
            <div className="card bg-dark text-white" style={{ borderRadius: '1rem' }}>
              <div className="card-body p-5 text-center">
                <div className="mb-md-5 mt-md-4 pb-5">
                  <h2 className="fw-bold mb-2 text-uppercase">Create an Account</h2>
                  <p className="text-white-50 mb-5">Please fill in the details to register!</p>

                  {message && (
                    <div className="alert alert-danger" role="alert">
                      {message}
                    </div>
                  )}

                  <form onSubmit={onSubmit}>
                    <div className="form-outline form-white mb-4">
                      <input
                        type="text"
                        id="fullName"
                        name="fullName"
                        className="form-control form-control-lg"
                        value={fullName}
                        onChange={onChange}
                        placeholder="John Doe"
                        required
                      />
                      <label className="form-label" htmlFor="fullName">Full Name</label>
                    </div>

                    <div className="form-outline form-white mb-4">
                      <input
                        type="text"
                        id="idNumber"
                        name="idNumber"
                        className="form-control form-control-lg"
                        value={idNumber}
                        onChange={onChange}
                        placeholder="12345678"
                        required
                      />
                      <label className="form-label" htmlFor="idNumber">ID Number</label>
                    </div>

                    <div className="form-outline form-white mb-4">
                      <input
                        type="text"
                        id="accountNumber"
                        name="accountNumber"
                        className="form-control form-control-lg"
                        value={accountNumber}
                        onChange={onChange}
                        placeholder="ACC12345"
                        required
                      />
                      <label className="form-label" htmlFor="accountNumber">Account Number</label>
                    </div>

                    <div className="form-outline form-white mb-4">
                      <input
                        type="password"
                        id="password"
                        name="password"
                        className="form-control form-control-lg"
                        value={password}
                        onChange={onChange}
                        placeholder="********"
                        required
                      />
                      <label className="form-label" htmlFor="password">Password</label>
                    </div>

                    <button
                      type="submit"
                      className="btn btn-outline-light btn-lg px-5"
                      disabled={loading}
                    >
                      {loading ? 'Registering...' : 'Register'}
                    </button>
                  </form>

                  <div className="d-flex justify-content-center text-center mt-4 pt-1">
                    <p className="text-white-50">
                      Already have an account?{' '}
                      <Link to="/login" className="fw-bold text-white">Login</Link>
                    </p>
                  </div>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Register;
