import React, { useEffect, useState } from 'react';

const Payments = () => {
    const [payments, setPayments] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch payments data here
        // Example: fetchPayments() to get data
        // After fetching data, set it in state
        // setPayments(fetchedData);
        // setLoading(false);
    }, []);

    const verifyPayment = (paymentId) => {
        // Logic for verifying the payment goes here
    };

    return (
        <div>
            <div className="payments-container">
                {/* Loading state */}
                {loading ? (
                    <p>Loading payments...</p>
                ) : (
                    <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
                        <thead>
                            <tr style={{ backgroundColor: '#38b2ac', color: 'white' }}>
                                <th style={{ padding: '12px', border: '1px solid #e2e8f0' }}>Client</th>
                                <th style={{ padding: '12px', border: '1px solid #e2e8f0' }}>Amount</th>
                                <th style={{ padding: '12px', border: '1px solid #e2e8f0' }}>Currency</th>
                                <th style={{ padding: '12px', border: '1px solid #e2e8f0' }}>Provider</th>
                                <th style={{ padding: '12px', border: '1px solid #e2e8f0' }}>Status</th>
                                <th style={{ padding: '12px', border: '1px solid #e2e8f0' }}>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {payments.map((payment) => (
                                <tr key={payment._id} style={{ textAlign: 'center', borderBottom: '1px solid #e2e8f0' }}>
                                    <td style={{ padding: '12px' }}>{payment.client?.name}</td>
                                    <td style={{ padding: '12px' }}>{payment.amount}</td>
                                    <td style={{ padding: '12px' }}>{payment.currency}</td>
                                    <td style={{ padding: '12px' }}>{payment.provider}</td>
                                    <td style={{ padding: '12px' }}>{payment.status}</td>
                                    <td style={{ padding: '12px' }}>
                                        {payment.status === 'pending' && (
                                            <button
                                                onClick={() => verifyPayment(payment._id)}
                                                style={{
                                                    padding: '8px 16px', backgroundColor: '#38b2ac', color: 'white', borderRadius: '4px', border: 'none', cursor: 'pointer'
                                                }}
                                            >
                                                Verify
                                            </button>
                                        )}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default Payments;
