import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div>
      {/* Hero Section */}
      <div
        style={{
          backgroundImage:
          "url('https://img.freepik.com/premium-photo/blue-digital-bank-glowing-icon-blue-tone-background-online-banking-app-transaction-concept-3d_117038-28415.jpg?w=1060')",
          backgroundPosition: 'center',
          backgroundRepeat: 'no-repeat',
          backgroundSize: 'cover',
          height: '600px',
          position: 'relative',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
        }}
      >
        <div
          style={{
            position: 'absolute',
            top: '0',
            left: '0',
            width: '100%',
            height: '100%',
            background: 'linear-gradient(to right, rgba(0,0,0,0.6), rgba(0,0,0,0.6))',
          }}
        />
        <div style={{ position: 'relative', textAlign: 'center', padding: '0 1rem' }}>
          <h1
            style={{
              fontSize: '2.5rem',
              marginBottom: '1rem',
              fontWeight: 'bold',
              textShadow: '2px 2px 4px rgba(0,0,0,0.6)',
            }}
          >
           Welcome to the Bank Portal
          </h1>
          <p style={{ fontSize: '1.25rem', marginBottom: '1.5rem', textShadow: '1px 1px 2px rgba(0,0,0,0.6)' }}>
            Your secure place for banking services
            Secure As A Safe
          </p>
          <Link
            to="/login"
            style={{
              backgroundColor: '#38b2ac',
              color: 'white',
              padding: '1rem 2rem',
              fontSize: '1.125rem',
              textDecoration: 'none',
              borderRadius: '0.375rem',
              transition: 'all 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'scale(1.05)';
              e.target.style.boxShadow = '0 4px 8px rgba(0,0,0,0.1)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'scale(1)';
              e.target.style.boxShadow = 'none';
            }}
          >
            Get Started
          </Link>
        </div>
      </div>

      {/* Features Section */}
      <div style={{ padding: '4rem 1rem', backgroundColor: '#f7fafc' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '3rem', fontSize: '2.25rem', fontWeight: 'bold' }}>
          Why Choose Us
        </h2>
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: '1fr',
            gap: '2rem',
            maxWidth: '1200px',
            margin: '0 auto',
          }}
        >
          {/* Feature 1 */}
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              backgroundColor: 'white',
              padding: '2rem',
              borderRadius: '0.375rem',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-5px)';
              e.target.style.boxShadow = '0 4px 16px rgba(0,0,0,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 2px 8px rgba(0,0,0,0.1)';
            }}
          >
            <div
              style={{
                width: '3rem',
                height: '3rem',
                backgroundColor: '#38b2ac',
                color: 'white',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginBottom: '1.5rem',
              }}
            >
              <span style={{ fontSize: '1.5rem' }}>🚀</span>
            </div>
            <h3 style={{ marginBottom: '0.5rem', fontSize: '1.25rem' }}>Fast Transactions</h3>
            <p style={{ textAlign: 'center', color: '#4a5568' }}>
              Transfer funds quickly to any country with our optimized payment processing system.
            </p>
          </div>

          {/* Feature 2 */}
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              backgroundColor: 'white',
              padding: '2rem',
              borderRadius: '0.375rem',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-5px)';
              e.target.style.boxShadow = '0 4px 16px rgba(0,0,0,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 2px 8px rgba(0,0,0,0.1)';
            }}
          >
            <div
              style={{
                width: '3rem',
                height: '3rem',
                backgroundColor: '#38b2ac',
                color: 'white',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginBottom: '1.5rem',
              }}
            >
              <span style={{ fontSize: '1.5rem' }}>🛡️</span>
            </div>
            <h3 style={{ marginBottom: '0.5rem', fontSize: '1.25rem' }}>Secure Payments</h3>
            <p style={{ textAlign: 'center', color: '#4a5568' }}>
              Your transactions are protected with state-of-the-art security protocols and encryption.
            </p>
          </div>

          {/* Feature 3 */}
          <div
            style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              backgroundColor: 'white',
              padding: '2rem',
              borderRadius: '0.375rem',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
              transition: 'transform 0.3s ease, box-shadow 0.3s ease',
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-5px)';
              e.target.style.boxShadow = '0 4px 16px rgba(0,0,0,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 2px 8px rgba(0,0,0,0.1)';
            }}
          >
            <div
              style={{
                width: '3rem',
                height: '3rem',
                backgroundColor: '#38b2ac',
                color: 'white',
                borderRadius: '50%',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                marginBottom: '1.5rem',
              }}
            >
              <span style={{ fontSize: '1.5rem' }}>🎧</span>
            </div>
            <h3 style={{ marginBottom: '0.5rem', fontSize: '1.25rem' }}>24/7 Support</h3>
            <p style={{ textAlign: 'center', color: '#4a5568' }}>
              Our dedicated support team is available around the clock to assist you with any inquiries.
            </p>
          </div>
        </div>
      </div>

      {/* Additional Section: Testimonials (Optional) */}
      <div style={{ padding: '4rem 1rem' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '3rem', fontSize: '2.25rem', fontWeight: 'bold' }}>
          What Our Users Say
        </h2>
        <div
          style={{
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <div
            style={{
              width: '100%',
              maxWidth: '600px',
              backgroundColor: 'white',
              padding: '2rem',
              borderRadius: '0.375rem',
              boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
            }}
          >
            <p style={{ fontSize: '1.125rem', marginBottom: '1rem' }}>
              "The most seamless international payment system I’ve ever used. Fast, secure, and reliable."
            </p>
            <p style={{ fontSize: '1rem', fontStyle: 'italic', color: '#4a5568' }}>John Doe, CEO</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
