export function clampTime(value: Time, min: Time | undefined, max: Time | undefined) {
  let time = value
  if (min && min.compare(value) > 0) {
    time = min.copy()
  } else if (max && max.compare(value) < 0) {
    time = max.copy()
  }
  return time
}
import { Time } from "@internationalized/date"
import { createMachine, guards } from "@zag-js/core"
import { trackDismissableElement } from "@zag-js/dismissable"
import { raf } from "@zag-js/dom-query"
import { getPlacement } from "@zag-js/popper"
import { compact, isEqual, match, next, prev } from "@zag-js/utils"
import { dom } from "./time-picker.dom"
import type { MachineContext, MachineState, TimeUnit, UserDefinedContext } from "./time-picker.types"
import {
  clampTime,
  getCurrentTime,
  getHourPeriod,
  getStringifiedValue,
  getTimeValue,
  is12HourFormat,
