import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/authcontext.mjs';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    identity: '',
    accountID: '',
    secret: '',
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const { identity, accountID, secret } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('https://localhost:5000/api/users/login', formData);
      login(res.data.token); // Update the AuthContext
      
      const payload = JSON.parse(atob(res.data.token.split('.')[1]));
      alert('Login successful. Redirecting...');
      
      setTimeout(() => {
        if (payload.role === 'staff') {
          navigate('/payments');
        } else {
          navigate('/dashboard');
        }
      }, 2000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Login failed.');
      alert(error.response?.data?.message || 'Invalid credentials. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="vh-100" style={{ backgroundColor: '#508bfc' }}>
      <div className="container py-5 h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-12 col-md-8 col-lg-6 col-xl-5">
            <div className="card shadow-2-strong" style={{ borderRadius: '1rem' }}>
              <div className="card-body p-5 text-center">
                <h3 className="mb-5">Sign in</h3>

                {message && (
                  <div className="alert alert-warning" role="alert">
                    <strong>Error:</strong> {message}
                  </div>
                )}

                <form onSubmit={onSubmit}>
                  <div className="form-outline mb-4">
                    <input
                      type="text"
                      name="identity"
                      value={identity}
                      onChange={onChange}
                      placeholder="Enter your ID Number"
                      className="form-control form-control-lg"
                    />
                    <label className="form-label">ID Number</label>
                  </div>

                  <div className="form-outline mb-4">
                    <input
                      type="text"
                      name="accountID"
                      value={accountID}
                      onChange={onChange}
                      placeholder="Enter your Account Number"
                      className="form-control form-control-lg"
                    />
                    <label className="form-label">Account Number</label>
                  </div>

                  <div className="form-outline mb-4">
                    <input
                      type="password"
                      name="secret"
                      value={secret}
                      onChange={onChange}
                      placeholder="Enter your Password"
                      className="form-control form-control-lg"
                    />
                    <label className="form-label">Password</label>
                  </div>

                  <button
                    type="submit"
                    className="btn btn-primary btn-lg btn-block"
                    style={{ opacity: loading ? 0.7 : 1 }}
                    disabled={loading}
                  >
                    {loading ? 'Logging in...' : 'Login'}
                  </button>

                  <hr className="my-4" />

                  <button
                    className="btn btn-lg btn-block"
                    style={{ backgroundColor: '#dd4b39', color: 'white', marginBottom: '1rem' }}
                    type="button"
                  >
                    <i className="fab fa-google me-2"></i> Sign in with Google
                  </button>
                  <button
                    className="btn btn-lg btn-block"
                    style={{ backgroundColor: '#3b5998', color: 'white' }}
                    type="button"
                  >
                    <i className="fab fa-facebook-f me-2"></i> Sign in with Facebook
                  </button>
                </form>

                <p style={{ marginTop: '1rem' }}>
                  Don't have an account?{' '}
                  <Link to="/register" style={{ color: '#2b6cb0' }}>
                    Register here
                  </Link>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
