import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/authcontext.mjs';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    identity: '',
    accountID: '',
    secret: '',
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const { identity, accountID, secret } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('https://localhost:5000/api/users/login', formData);
      login(res.data.token); // Update the AuthContext
      
      const payload = JSON.parse(atob(res.data.token.split('.')[1]));
      alert('Login successful. Redirecting...');
      
      // Redirect based on user type
      setTimeout(() => {
        if (payload.role === 'staff') {
          navigate('/payments');
        } else {
          navigate('/dashboard');
        }
      }, 2000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Login failed.');
      alert(error.response?.data?.message || 'Invalid credentials. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="vh-100 gradient-custom">
      <div className="container py-5 h-100">
        <div className="row d-flex justify-content-center align-items-center h-100">
          <div className="col-12 col-md-8 col-lg-6 col-xl-5">
            <div className="card bg-dark text-white" style={{ borderRadius: '1rem' }}>
              <div className="card-body p-5 text-center">

                <div className="mb-md-5 mt-md-4 pb-5">
                  <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
                  <p className="text-white-50 mb-5">Please enter your login and password!</p>

                  {message && (
                    <div style={{ backgroundColor: '#fbd38d', color: '#9b2c2c', padding: '1rem', borderRadius: '8px', marginBottom: '1rem' }}>
                      <strong>Error:</strong> {message}
                    </div>
                  )}

                  <div className="form-outline form-white mb-4">
                    <input
                      type="text"
                      name="identity"
                      value={identity}
                      onChange={onChange}
                      placeholder="Enter your ID Number"
                      className="form-control form-control-lg"
                      style={{ padding: '0.75rem', borderRadius: '8px' }}
                    />
                    <label className="form-label" htmlFor="identity">ID Number</label>
                  </div>

                  <div className="form-outline form-white mb-4">
                    <input
                      type="text"
                      name="accountID"
                      value={accountID}
                      onChange={onChange}
                      placeholder="Enter your Account Number"
                      className="form-control form-control-lg"
                      style={{ padding: '0.75rem', borderRadius: '8px' }}
                    />
                    <label className="form-label" htmlFor="accountID">Account Number</label>
                  </div>

                  <div className="form-outline form-white mb-4">
                    <input
                      type="password"
                      name="secret"
                      value={secret}
                      onChange={onChange}
                      placeholder="Enter your secret"
                      className="form-control form-control-lg"
                      style={{ padding: '0.75rem', borderRadius: '8px' }}
                    />
                    <label className="form-label" htmlFor="secret">Password</label>
                  </div>

                  <button
                    type="submit"
                    className="btn btn-outline-light btn-lg px-5"
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      opacity: loading ? 0.7 : 1,
                    }}
                    disabled={loading}
                  >
                    {loading ? 'Logging in...' : 'Login'}
                  </button>

                  <p className="small mb-5 pb-lg-2">
                    <a className="text-white-50" href="#!">Forgot password?</a>
                  </p>

                  <div className="d-flex justify-content-center text-center mt-4 pt-1">
                    <a href="#!" className="text-white"><i className="fab fa-facebook-f fa-lg"></i></a>
                    <a href="#!" className="text-white"><i className="fab fa-twitter fa-lg mx-4 px-2"></i></a>
                    <a href="#!" className="text-white"><i className="fab fa-google fa-lg"></i></a>
                  </div>

                </div>

                <div>
                  <p className="mb-0">Don't have an account? 
                    <Link to="/register" className="text-white-50 fw-bold"> Sign Up</Link>
                  </p>
                </div>

              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
