      created: ["setPreviousPanels", "setInitialSize"],

      watch: {
        size: ["setActiveResizeState"],
      },

      computed: {
        isHorizontal: (ctx) => ctx.orientation === "horizontal",
        panels: (ctx) => getNormalizedPanels(ctx),
      },

      on: {
        SET_PANEL_SIZE: {
          actions: "setPanelSize",
