      states: {
        idle: {
          entry: ["clearActiveHandleId"],
          on: {
            POINTER_OVER: {
              target: "hover:temp",
              actions: ["setActiveHandleId"],
            },
            FOCUS: {
              target: "focused",
              actions: ["setActiveHandleId"],
            },
            DOUBLE_CLICK: {
              actions: ["resetStartPanel", "setPreviousPanels"],
