          on: {
            POINTER_DOWN: {
              target: "dragging",
              actions: ["setActiveHandleId"],
            },
            POINTER_LEAVE: "idle",
          },
        },

        hover: {
          tags: ["focus"],
          on: {
            POINTER_DOWN: "dragging",
            POINTER_LEAVE: "idle",
