import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/authcontext.mjs';

const Login = () => {
    const navigate = useNavigate();
    const { login } = useContext(AuthContext);
    const [formData, setFormData] = useState({ identity: '', accountID: '', secret: '' });
    const [message, setMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const res = await axios.post('https://localhost:5000/api/users/login', formData);
            login(res.data.token);
            const payload = JSON.parse(atob(res.data.token.split('.')[1]));
            setTimeout(() => {
                if (payload.userType === 'staff') navigate('/payments');
                else navigate('/dashboard');
            }, 2000);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Login failed.');
            alert(error.response?.data?.message || 'Invalid credentials. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Login to Your Account</h2>
                {message && <div className="error">{message}</div>}
                <form onSubmit={onSubmit}>
                    <input type="text" name="identity" value={formData.identity} onChange={onChange} placeholder="ID Number" />
                    <input type="text" name="accountID" value={formData.accountID} onChange={onChange} placeholder="Account Number" />
                    <input type="password" name="secret" value={formData.secret} onChange={onChange} placeholder="Password" />
                    <button type="submit" disabled={loading}>{loading ? 'Logging in...' : 'Login'}</button>
                </form>
                <p>Don't have an account? <Link to="/register">Register here</Link></p>
            </div>
        </div>
    );
};

export default Login;
