import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/authcontext.mjs';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);
  const [formData, setFormData] = useState({
    identity: '',
    accountID: '',
    secret: '',
  });
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const { identity, accountID, secret } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('https://localhost:5000/api/users/login', formData);
      login(res.data.token); // Update the AuthContext
      
      const payload = JSON.parse(atob(res.data.token.split('.')[1]));
      alert('Login successful. Redirecting...');
      
      // Redirect based on user type
      setTimeout(() => {
        if (payload.role === 'staff') {
          navigate('/payments');
        } else {
          navigate('/dashboard');
        }
      }, 2000);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Login failed.');
      alert(error.response?.data?.message || 'Invalid credentials. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section
      className="vh-100 bg-image"
      style={{ backgroundImage: 'url("https://mdbcdn.b-cdn.net/img/Photos/new-templates/search-box/img4.webp")' }}
    >
      <div className="mask d-flex align-items-center h-100 gradient-custom-3">
        <div className="container h-100">
          <div className="row d-flex justify-content-center align-items-center h-100">
            <div className="col-12 col-md-9 col-lg-7 col-xl-6">
              <div className="card" style={{ borderRadius: '15px' }}>
                <div className="card-body p-5">
                  <h2 className="text-uppercase text-center mb-5">Login to Your Account</h2>

                  {message && (
                    <div
                      style={{
                        backgroundColor: '#fbd38d',
                        color: '#9b2c2c',
                        padding: '1rem',
                        borderRadius: '8px',
                        marginBottom: '1rem',
                      }}
                    >
                      <strong>Error:</strong> {message}
                    </div>
                  )}

                  <form onSubmit={onSubmit}>
                    <div className="form-outline mb-4">
                      <input
                        type="text"
                        name="identity"
                        value={identity}
                        onChange={onChange}
                        className="form-control form-control-lg"
                        placeholder="Enter your ID Number"
                      />
                      <label className="form-label">ID Number</label>
                    </div>

                    <div className="form-outline mb-4">
                      <input
                        type="text"
                        name="accountID"
                        value={accountID}
                        onChange={onChange}
                        className="form-control form-control-lg"
                        placeholder="Enter your Account Number"
                      />
                      <label className="form-label">Account Number</label>
                    </div>

                    <div className="form-outline mb-4">
                      <input
                        type="password"
                        name="secret"
                        value={secret}
                        onChange={onChange}
                        className="form-control form-control-lg"
                        placeholder="Enter your secret"
                      />
                      <label className="form-label">Password</label>
                    </div>

                    <button
                      type="submit"
                      className="btn btn-success btn-block btn-lg gradient-custom-4 text-body"
                      style={{ width: '100%', opacity: loading ? 0.7 : 1 }}
                      disabled={loading}
                    >
                      {loading ? 'Logging in...' : 'Login'}
                    </button>
                  </form>

                  <p className="text-center text-muted mt-5 mb-0">
                    Don't have an account?{' '}
                    <Link to="/register" className="fw-bold text-body">
                      <u>Register here</u>
                    </Link>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Login;
